    public int findStopCodon(String dna, int startIndex, String stopCodon ) {
        int stopIndex = dna.indexOf(stopCodon, startIndex+3); 
        int currStartIndex = stopIndex;
                     
            while ( currStartIndex != -1) {
                int geneLength = stopIndex -startIndex;
                if (geneLength %3==0) {
                   return stopIndex;
                }
                
                if (geneLength%3!=0) {
                    currStartIndex = stopIndex+1;
                    stopIndex = dna.indexOf(stopCodon, currStartIndex);
                    if (stopIndex == -1) {
                      stopIndex = dna.length();
                      return stopIndex;
                    }
                }
                             
            }
            
            return dna.length();
        }
        
    public String findGene(String dna, int startHere) {
        
        if ( dna.toUpperCase() == dna) {
            dna = dna;
        }
        
        if ( dna.toUpperCase() != dna) {
            dna = dna.toUpperCase();
        }
        String gene ="";
        String startCodon = "ATG";
        int startIndex = dna.indexOf(startCodon, startHere);
        if (startIndex == -1){
            return gene;
        }
        String stopCodon = "TAA";
        int TAAstopIndex = findStopCodon(dna,startIndex,stopCodon);
        stopCodon = "TAG";
        int TAGstopIndex = findStopCodon(dna,startIndex,stopCodon);
        stopCodon = "TGA";
        int TGAstopIndex = findStopCodon(dna,startIndex,stopCodon);
        
        int minIndex = Math.min( TAAstopIndex, (Math.min(TAGstopIndex,TGAstopIndex)));
        int testStopIndex = minIndex + 3;
                
            if (minIndex==dna.length()){
                gene ="";
                return gene;
            }
            
        gene = dna.substring(startIndex, minIndex + 3);   
               
         if (gene.length()==dna.length()) {
            
             if (gene.length()==6) {
                if (gene.endsWith("TAA")) {
                    return gene;
                }
                
                if (gene.endsWith("TAG")) {
                    return gene;
                }
                
                if (gene.endsWith("TGA")) {
                    return gene;
                }
            
            }           
            
            gene ="";
            return gene;
        }
        
        return gene;
    }
      
    
    
    public StorageResource getAllGenes(String dna ){
        StorageResource geneList = new StorageResource();
        int startIndex =0;
        int count = 0; 
         while ( true ){
           String currGene = findGene(dna, startIndex);
           if ( currGene=="") { 
               break;
            }
           geneList.add(currGene);
           count = count +1;
           startIndex = dna.indexOf(currGene,startIndex) + currGene.length();
        }
        return geneList;
    }
    
    public double cgRatio( String dna) {
        double countCG = 0;
        int startIndexC = 0;
        int startIndexG =0;   
        
        while (dna.indexOf("C",startIndexC) != -1) {
            countCG += 1;
            startIndexC = dna.indexOf("C",startIndexC) +1;
        }
        
        while (dna.indexOf("G",startIndexG) != -1) {
            countCG += 1;
            startIndexG = dna.indexOf("G",startIndexG) +1;
        }
         double cgRatio = countCG/dna.length();  
         return cgRatio;
    }
    
    public int ctgCount (String dna) {
        int ctgCount = 0;
        int startIndex =0;
        
        while ( dna.indexOf("CTG", startIndex) != -1) {
           ctgCount +=1;
           startIndex= dna.indexOf("CTG", startIndex)+3;
         }  
        return ctgCount;
    }
    
    public void processGenes (StorageResource sr) {
        int longer9Count = 0;
        int cg35Count = 0;
        int longestGene = 0;
        System.out.println("Number of Strings ;" + sr.size());
        for (String s : sr.data()) {
                if (s.length() > 60) {
                    longer9Count +=1;                                                
            }
            
             if (cgRatio(s) > 0.35) {
                    cg35Count +=1;                                           
            }
            
             if (s.length() > longestGene) {
                    longestGene = s.length();            
            }
            
        }
            System.out.println("Number longer than 60  : " + longer9Count);
            System.out.println("Number more than 0.35  : " +cg35Count); 
            System.out.println("The lenght of the longest Gene is:" + longestGene);
    }

         
    
